import React from "react";

class Form extends React.Component {
   state = {
       estadoFavorito:'',
       email:'',
       nome:'',
       idade: 0,
       vaiComparecer: false, 
       palavraChave:''
   }

   handleChange = ({ target }) =>{
     // event.target = ({ target }) - descontruido no parâmetro
     // console.log(target) - retorna o elemento do input com o name e o valor
     //  console.log (target.value) - retorna o valor colocado no input alterado
     //  const { name, } = target // event.target 
      const value = target.type === 'checkbox' ? target.checked : target.value
      this.setState(
        { 
          [target.name]: value 
        })
   }

    render () {
        return (
        <div className="formulario">
            <h1>Estados e React - Tecnologia fantástica ou reagindo a regionalismos?</h1>
            <form className="form">
              <label htmlFor="estadoFavorito">
                  <p>Diga qual o seu Estado favorito! </p>
                  <textarea 
                  name="estadoFavorito"
                  id="estadoFavorito"
                  value={ this.state.estadoFavorito } // 'pega o valor do estado '
                  onChange={ this.handleChange } // "chama a função que altera o Estado"
                />
              </label>
              <label>
                  <p>Email: </p>
                  <input 
                  type="email"
                  name ="email"
                  value={ this.state.email } 
                  onChange={ this.handleChange } 
                  />
              </label>
              <label>
                  <p>Nome: </p>
                  <input
                  type="text"
                  name="nome"
                  value={ this.state.nome } 
                  onChange={ this.handleChange } 
                  />
              </label>
              <label>
                 <p>Idade: </p>
                 <input
                   type="number"
                   name="idade"
                   value={ this.state.idade } 
                   onChange={ this.handleChange } 
                 />
              </label>
              <label>
                 <p>Vai comparecer? </p> 
                 <input
                    type="checkbox"
                    name="vaiComparecer"
                    value={ this.state.vaiComparecer } 
                    onChange={ this.handleChange } 
                 />
              </label>
              <label>
                  <p>Escolha sua palavra chave favorita!</p>
                  <select 
                    name="palavraChave"  
                    value={ this.state.palavraChave } 
                    onChange={ this.handleChange } > 
                  <option value="estado"> Estado </option>
                  <option value="evento">Evento</option>
                  <option value="props">Props</option>
                  <option value="hooks">Hooks</option>
                  </select>
              </label>
            </form>
          </div>
        );
    }
}

export default Form
