import React from "react";

class NameInput extends React.Component {
    render () {
        const { names, onInputChange } = this.props
        return (    <label>
            <p>Nome: </p>
            <input
            type="text"
            name="nome"
            value={ names.toUpperCase() } 
            onChange={ onInputChange } 
            />
        </label>);
    }
}

export default NameInput;
